mapper.select:
SELECT id, name, phone, if((select count(*) from user_admin where id = #{id}) = 1, "Y", "N") admin
FROM happyhouse.user
where id = #{id};

mapper.update:
update user
set name  = #{name},
    phone = #{phone}
where id = #{id}

mapper.select:
select id, name, phone
from user
where id = #{id}
  and password = #{password}
